class Solution {
public:
    int countTexts(string pressedKeys) {
        int m = 1000000007;
        vector<long long> dp3 = {1, 1, 2, 4};   // è¿ç»­æŒ‰å¤šæ¬¡ 3 ä¸ªå­—æ¯æŒ‰é”®å¯¹åº”çš„æ–¹æ¡ˆæ•°
        vector<long long> dp4 = {1, 1, 2, 4};   // è¿ç»­æŒ‰å¤šæ¬¡ 4 ä¸ªå­—æ¯æŒ‰é”®å¯¹åº”çš„æ–¹æ¡ˆæ•°
        int n = pressedKeys.size();
        for (int i = 4; i < n + 1; ++i) {
            dp3.push_back((dp3[i-1] + dp3[i-2] + dp3[i-3]) % m);
            dp4.push_back((dp4[i-1] + dp4[i-2] + dp4[i-3] + dp4[i-4]) % m);
        }
        long long res = 1;   // æ€»æ–¹æ¡ˆæ•°
        int cnt = 1;   // å½“å‰å­—ç¬¦è¿ç»­å‡ºç°çš„æ¬¡æ•°
        for (int i = 1; i < n; ++i) {
            if (pressedKeys[i] == pressedKeys[i-1]) {
                ++cnt;
            } else {
                // å¯¹æŒ‰é”®å¯¹åº”å­—ç¬¦æ•°é‡è®¨è®ºå¹¶æ›´æ–°æ€»æ–¹æ¡ˆæ•°
                if (pressedKeys[i-1] == '7' || pressedKeys[i-1] == '9') {
                    res *= dp4[cnt];
                } else {
                    res *= dp3[cnt];
                }
                res %= m;
                cnt = 1;
            }
        }
        // æ›´æ–°æœ€åä¸€æ®µè¿ç»­å­—ç¬¦å­ä¸²å¯¹åº”çš„æ–¹æ¡ˆæ•°
        if (pressedKeys[n-1] == '7' || pressedKeys[n-1] == '9') {
            res *= dp4[cnt];
        } else {
            res *= dp3[cnt];
        }
        res %= m;
        return res;
    }
};
