class Solution {
public:
    int maxSubarraySumCircular(vector<int>& nums) {
        int n=nums.size();
        vector<int> qianmax(n); //å‰ç¼€æœ€å¤§å€¼
        int presum=nums[0];
        qianmax[0]=nums[0];

        int pre=nums[0];
        int ans=pre;

        for(int i=1;i<n;i++){
            pre=max(pre,0)+nums[i];
            ans=max(ans,pre);

            presum+=nums[i];
            qianmax[i]=max(qianmax[i-1],presum);
        }

        int housum=0;
        for(int i=n-1;i>0;i--){ //æšä¸¾åç¼€å’Œ
            housum+=nums[i];
            ans=max(ans,housum+qianmax[i-1]);
        }
        return ans;
    }
};
```
