class Solution {
public:
    int jewelleryValue(vector<vector<int>>& frame) {
        int n=frame.size();
        int m=frame[0].size();
        vector<vector<int>> f(n,vector<int>(m));
        f[0][0]=frame[0][0];
        for(int i=1;i<n;i++){ //å¤„ç†ç¬¬ä¸€åˆ—
            f[i][0]=f[i-1][0]+frame[i][0];
        }
        for(int j=1;j<m;j++){ //å¤„ç†ç¬¬ä¸€è¡Œ
            f[0][j]=f[0][j-1]+frame[0][j];
        }
        for(int i=1;i<n;i++){
            for(int j=1;j<m;j++){
                f[i][j]=max(f[i-1][j],f[i][j-1])+frame[i][j];
            }
        }
        return f[n-1][m-1];
